/**
 * Calculator Application
 * 
 * A simple calculator for WebOS with basic arithmetic operations.
 */

class Calculator {
  constructor() {
    this.name = 'Calculator';
    this.version = '1.0.0';
    this.display = '0';
    this.currentValue = 0;
    this.previousValue = 0;
    this.operation = null;
    this.newNumber = true;
  }

  /**
   * Initialize the calculator
   */
  init() {
    console.log(`${this.name} v${this.version} initialized`);
    this.render();
  }

  /**
   * Handle number button press
   */
  inputNumber(num) {
    if (this.newNumber) {
      this.display = String(num);
      this.newNumber = false;
    } else {
      this.display += String(num);
    }
    this.currentValue = parseFloat(this.display);
  }

  /**
   * Handle operation button press
   */
  inputOperation(op) {
    if (this.operation && !this.newNumber) {
      this.calculate();
    }
    this.previousValue = this.currentValue;
    this.operation = op;
    this.newNumber = true;
  }

  /**
   * Calculate result
   */
  calculate() {
    let result = 0;

    switch (this.operation) {
      case '+':
        result = this.previousValue + this.currentValue;
        break;
      case '-':
        result = this.previousValue - this.currentValue;
        break;
      case '*':
        result = this.previousValue * this.currentValue;
        break;
      case '/':
        if (this.currentValue !== 0) {
          result = this.previousValue / this.currentValue;
        } else {
          result = 'Error';
        }
        break;
      default:
        result = this.currentValue;
    }

    this.display = String(result);
    this.currentValue = result;
    this.operation = null;
    this.newNumber = true;
  }

  /**
   * Clear calculator
   */
  clear() {
    this.display = '0';
    this.currentValue = 0;
    this.previousValue = 0;
    this.operation = null;
    this.newNumber = true;
  }

  /**
   * Render the calculator UI
   */
  render() {
    return {
      type: 'window',
      title: this.name,
      width: 250,
      height: 350,
      content: {
        type: 'div',
        className: 'calculator',
        children: [
          {
            type: 'div',
            className: 'display',
            text: this.display
          },
          {
            type: 'div',
            className: 'buttons',
            children: [
              { type: 'button', text: '7', onClick: () => this.inputNumber(7) },
              { type: 'button', text: '8', onClick: () => this.inputNumber(8) },
              { type: 'button', text: '9', onClick: () => this.inputNumber(9) },
              { type: 'button', text: '/', onClick: () => this.inputOperation('/') },
              { type: 'button', text: '4', onClick: () => this.inputNumber(4) },
              { type: 'button', text: '5', onClick: () => this.inputNumber(5) },
              { type: 'button', text: '6', onClick: () => this.inputNumber(6) },
              { type: 'button', text: '*', onClick: () => this.inputOperation('*') },
              { type: 'button', text: '1', onClick: () => this.inputNumber(1) },
              { type: 'button', text: '2', onClick: () => this.inputNumber(2) },
              { type: 'button', text: '3', onClick: () => this.inputNumber(3) },
              { type: 'button', text: '-', onClick: () => this.inputOperation('-') },
              { type: 'button', text: '0', onClick: () => this.inputNumber(0) },
              { type: 'button', text: 'C', onClick: () => this.clear() },
              { type: 'button', text: '=', onClick: () => this.calculate() },
              { type: 'button', text: '+', onClick: () => this.inputOperation('+') }
            ]
          }
        ]
      }
    };
  }

  /**
   * Clean up when app is closed
   */
  destroy() {
    console.log(`${this.name} destroyed`);
  }
}

// Export the app
if (typeof module !== 'undefined' && module.exports) {
  module.exports = Calculator;
}
