/**
 * NotePad Application
 * 
 * A simple text editor for WebOS.
 */

class NotePad {
  constructor() {
    this.name = 'NotePad';
    this.version = '1.0.0';
    this.content = '';
    this.fileName = 'Untitled';
    this.isDirty = false;
  }

  /**
   * Initialize the notepad
   */
  init() {
    console.log(`${this.name} v${this.version} initialized`);
    this.render();
  }

  /**
   * Handle text change
   */
  onTextChange(text) {
    this.content = text;
    this.isDirty = true;
  }

  /**
   * Create new document
   */
  newDocument() {
    if (this.isDirty) {
      const confirm = window.confirm('Discard unsaved changes?');
      if (!confirm) return;
    }
    this.content = '';
    this.fileName = 'Untitled';
    this.isDirty = false;
  }

  /**
   * Save document
   */
  async save() {
    try {
      // In a real implementation, this would use the WebOS file API
      console.log('Saving document:', this.fileName);
      // Simulate save
      await this.saveToStorage(this.fileName, this.content);
      this.isDirty = false;
      return true;
    } catch (error) {
      console.error('Failed to save:', error);
      return false;
    }
  }

  /**
   * Save to app storage
   */
  async saveToStorage(fileName, content) {
    // This would integrate with the WebOS SDK storage API
    return new Promise((resolve) => {
      setTimeout(() => {
        console.log(`Saved ${fileName} (${content.length} characters)`);
        resolve();
      }, 100);
    });
  }

  /**
   * Open document
   */
  async open(fileName) {
    try {
      // In a real implementation, this would use the WebOS file API
      console.log('Opening document:', fileName);
      // Simulate load
      const content = await this.loadFromStorage(fileName);
      this.content = content;
      this.fileName = fileName;
      this.isDirty = false;
      return true;
    } catch (error) {
      console.error('Failed to open:', error);
      return false;
    }
  }

  /**
   * Load from app storage
   */
  async loadFromStorage(fileName) {
    // This would integrate with the WebOS SDK storage API
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve('Sample content loaded from ' + fileName);
      }, 100);
    });
  }

  /**
   * Get word count
   */
  getWordCount() {
    if (!this.content) return 0;
    return this.content.trim().split(/\s+/).filter(word => word.length > 0).length;
  }

  /**
   * Get character count
   */
  getCharCount() {
    return this.content.length;
  }

  /**
   * Render the notepad UI
   */
  render() {
    return {
      type: 'window',
      title: `${this.name} - ${this.fileName}${this.isDirty ? ' *' : ''}`,
      width: 600,
      height: 400,
      content: {
        type: 'div',
        className: 'notepad',
        children: [
          {
            type: 'div',
            className: 'toolbar',
            children: [
              { type: 'button', text: 'New', onClick: () => this.newDocument() },
              { type: 'button', text: 'Open', onClick: () => this.open() },
              { type: 'button', text: 'Save', onClick: () => this.save() }
            ]
          },
          {
            type: 'textarea',
            className: 'editor',
            value: this.content,
            onChange: (e) => this.onTextChange(e.target.value),
            placeholder: 'Start typing...'
          },
          {
            type: 'div',
            className: 'statusbar',
            children: [
              {
                type: 'span',
                text: `Words: ${this.getWordCount()} | Characters: ${this.getCharCount()}`
              }
            ]
          }
        ]
      }
    };
  }

  /**
   * Clean up when app is closed
   */
  destroy() {
    if (this.isDirty) {
      const confirm = window.confirm('Discard unsaved changes?');
      if (!confirm) return false;
    }
    console.log(`${this.name} destroyed`);
    return true;
  }
}

// Export the app
if (typeof module !== 'undefined' && module.exports) {
  module.exports = NotePad;
}
